{
    fromAttribute(value) {
        if (typeof value !== 'string') {
            return null;
        }
        else {
            return value.split(',').map(x => x.trim()).map(x => parseInt(x, 10));
        }
    },
    toAttribute(value) {
        return value.join(',');
    },
}

{
  fromAttribute(value: string) {
    if (typeof value !== 'string') {
      return null;
    } else {
      return value.split(',').map(x => x.trim()).map(x => parseInt(x, 10));
    }
  },
  toAttribute(value: number[]) {
