document.body.addEventListener('close', event => {
  if (event instanceof AlertCloseEvent) {
    if (event.target.dataset.onClose === 'prevent-default') {
      console.log('prevent default');
      event.preventDefault();
    } else {
      return console.log('close');
    }
  }
});

