demo/rh-card-color-context-inline-0.css.content:
rh-context-demo::part(demo) {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(360px, 1fr));
  gap: var(--rh-space-lg);
}

rh-card {
  &.full {
    &::part(header) { margin: 0; }
    & :is(h1, h2, h3, h4, h5, h6)[slot="header"] {
      padding-inline: var(--rh-space-xl);
    }
    & img {
      display: block;
      min-width: 100%;
      height: auto;
    }
  }
  &.bar {
    --rh-card-header-background-on-light: var(--rh-color-surface-light);
    --rh-card-header-background-on-dark: var(--rh-color-surface-dark);
    &::part(header) {
      margin: 0;
      padding: var(--rh-space-lg) var(--rh-space-2xl);
      flex-direction: row;
      gap: var(--rh-space-lg);
      font-weight: var(--rh-font-weight-heading-regular);
      font-size: var(--rh-font-size-body-text-md);
      font-family: var(--rh-font-family-body-text);
    }
  }
}

label[for="picker"] {
  display: block;
}

demo/rh-card-grid-inline-1.css.content:
#card-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: var(--rh-space-lg, 16px);
  max-width: 1058px;
}

demo/rh-card-promo-full-width-inline-1.c.content:
rh-surface:first-of-type {
  padding-block-start: var(--rh-space-lg, 16px);
}

#full-width {
  container: full-width / inline-size;
  display: grid;
  gap: var(--rh-space-lg, 16px);
  grid-auto-rows: max-content;
}

@container full-width (min-width: 768px) {
  .reverse::part(container) {
    grid-template-columns: 1fr 2fr;
  }
  .reverse::part(image) {
    justify-self: start;
    order: -10;
  }
}

demo/rh-card-promo-narrow-inline-1.css.content:
#narrow {
  width: 296px;
  display: flex;
  flex-flow: column;
  gap: var(--rh-space-xl, 24px);
}

demo/rh-card-promo-standard-inline-1.css.content:
#standard {
  container-type: inline-size;
  container-name: standard;
  display: flex;
  flex-flow: column;
  gap: var(--rh-space-xl, 24px);
  margin: var(--rh-space-lg, 16px);
}

@container standard (min-width: 768px) {
  .reverse::part(footer) {
    order: -10;
  }
}

demo/rh-card-promo-inline-1.css.content:
main {
  padding: var(--rh-space-lg, 16px);
}

rh-card[variant="promo"] {
  container-name: promo;
  container-type: inline-size;
  margin-block-end: var(--rh-space-3xl, 48px);
}

@container promo (min-width: 768px) {
  .reverse::part(container) {
    grid-template-columns: 1fr 2fr;
  }

  .reverse::part(image) {
    justify-self: start;
    order: -10;
  }
}

demo/rh-card-sticky-pattern-inline-0.css.content:
#card-pattern-sticky {
  display: grid;
  gap: var(--rh-space-md);
  grid-template-columns: repeat(auto-fill, minmax(360px, 1fr));
  & rh-card {
    &::part(header) {
      gap: var(--rh-space-xl);
      flex-flow: column;
    }
    & .title {
      font-family: var(--rh-font-family-body-text);
      font-size: var(--rh-font-size-body-text-md);
    }
    & > p {
      margin-block: 0;
    }
  }
}

demo/rh-card-title-pattern-inline-0.css.content:
svg.sample {
  border: 1px dashed var(--rh-color-border-interactive-on-light, #0066cc);
  & rect { width: 100%; height: 100%; fill: rgba(0 102 204 / 0.10) }
}
#card-pattern-title {
  display: grid;
  gap: var(--rh-space-md, 16px);
  grid-template-columns: repeat(auto-fill, minmax(360px, 1fr));
  & rh-card,
  &::part(container) {
    height: 100%;
    &::part(header) {
      margin: 0;
      padding:
        var(--rh-space-lg, 16px)
        var(--rh-space-2xl, 32px);
      background-color: var(--rh-color-surface-light, #e0e0e0);
    }
    &::part(body) {
      display: flex;
      flex-flow: column;
    }
    & h2 {
      font-family: var(--rh-font-family-body-text);
      font-size: var(--rh-font-size-body-text-md);
      font-weight: var(--rh-font-weight-body-text-regular);
    }
    & svg {
      align-self: center;
      margin-block-end: var(--rh-space-2xl);
    }
  }
}

demo/rh-card-variants-inline-1.css.content:
#card-patterns {
  padding: 0.5em;
  display: grid;
  gap: 1em;
  grid-template-columns: repeat(auto-fit, minmax(360px, 1fr));
  grid-auto-columns: 1fr;
}

rh-card {
  height: max-content;
}

rh-card.bar {
  --rh-card-heading-font-family: var(--rh-font-family-body-text);
  --rh-card-heading-font-weight: var(--rh-font-weight-body-text-regular);
  --rh-card-heading-font-size: var(--rh-font-size-body-text-md);
  &::part(header) {
    margin: 0;
    padding: var(--rh-space-lg) var(--rh-space-xl);
    background-color: var(--_header-bg, var(--rh-color-surface-light));
  }
}

rh-card.image {
  --rh-card-heading-font-weight: var(--rh-font-weight-heading-medium);
  --rh-card-heading-font-size: var(--rh-font-size-heading-md);
  &::part(header) {
    margin: 0;
  }

  & img {
    display: block;
    min-width: 100%;
    height: auto;
  }

  &.heading {
    & h2 {
      margin-block-start: 0;
      margin-block-end: var(--rh-space-lg);
    }
  }
}

rh-card.center::part(body),
rh-card.center::part(footer) {
  justify-content: center;
  text-align: center;
}

rh-card.end::part(body),
rh-card.end::part(footer) {
  justify-content: end;
  text-align: end;
}

demo/rh-card-video-pattern-inline-0.css.content:
svg.sample {
  border: 1px dashed var(--rh-color-border-interactive-on-light, #0066cc);
  & rect { width: 100%; height: 100%; fill: rgba(0 102 204 / 0.10) }
}
#card-pattern-quote {
  display: grid;
  gap: var(--rh-space-md, 16px);
  grid-template-columns: repeat(auto-fill, minmax(360px, 1fr));
  & rh-card,
  &::part(container) {
    height: 100%;
    & rh-video {
      width: 100%;
      aspect-ratio: 9/5;
      /* below presumably implemented in rh-video */
      display: block;
      background-color: var(--rh-color-surface-light, #e0e0e0);
    }
  }
}

demo/reset.css.content:
/* Box sizing rules */
*,
*:before,
*:after {
  box-sizing: border-box;
}

/* Remove default margin */
:where(body,
h1,
h2,
h3,
h4,
h5,
h6,
p,
li,
figure,
figcaption,
blockquote,
dl,
dd) {
  margin: 0;
}

/* Default scroll behavior */
html:focus-within {
  scroll-behavior: smooth;
}

/* Inherit fonts for inputs and buttons */
:where(input,
button,
textarea,
select) {
  font: inherit;
}

/* Remove all animations and transitions for people that prefer not to see them */
@media (prefers-reduced-motion: reduce) {
  html:focus-within {
    scroll-behavior: auto;
  }

  *,
  *:before,
  *:after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }
}

