#jump-links-demo-container {
  display: grid;
  grid-template-columns: max-content auto;
  gap: var(--rh-space-lg);

  aside {
    h2 {
      font-weight: var(--rh-font-weight-heading-medium);
      font-family: var(--rh-font-family-body-text);
      font-size: var(--rh-font-size-body-text-md);
      line-height: var(--rh-line-height-body-text, 1.5);
      margin-block-end: var(--rh-space-lg);
    }
  }
}

demo/rh-jump-links-horizontal-inline-1.c.content:
#jump-links-horizontal-demo-page-content {
  display: block;

  #jump-links-container,
  article {
    background-color: var(--rh-color-surface);
  }

  article {
    padding: var(--rh-space-2xl);
  }

  #jump-links-container {
    position: sticky;
    padding-inline: var(--rh-space-2xl);
    padding-block-start: var(--rh-space-2xl);
    inset-block-start: 0;
  }

  #jump-links-title {
    font-weight: var(--rh-font-weight-heading-medium);
    font-family: var(--rh-font-family-body-text);
    font-size: var(--rh-font-size-body-text-md);
    line-height: var(--rh-line-height-body-text, 1.5);
    text-align: center;
    margin: 0 auto;
    margin-block-end: var(--rh-space-lg);
  }

  h2 {
    scroll-margin-block-start: 150px;
  }
}

demo/rh-jump-links-nested-inline-1.css.content:
#page-content {
  display: grid;
  grid-template-columns: max-content max-content;
  gap: var(--rh-space-lg);
  #jump-links-container, article {
    padding: var(--rh-space-2xl);
  }
  #jump-links-container {
    position: sticky;
    inset-block-start: 0;
    align-self: start;
  }
}
#jump-links-title {
  font-weight: var(--rh-font-weight-heading-medium);
  font-family: var(--rh-font-family-body-text);
  font-size: var(--rh-font-size-body-text-md);
