#responsive-container {
  margin: var(--rh-space-xl);

  & p { margin-block-end: var(--rh-space-md); }

  & h2 {
    font-size: var(--rh-font-size-heading-xl);
    font-weight: var(--rh-font-weight-heading-regular);
    font-family: var(--rh-font-family-heading);
    margin-block: var(--rh-space-md);
  }

  & .jump-links-title {
    rh-disclosure & { display: inline; }
    font-weight: var(--rh-font-weight-heading-medium);
    font-family: var(--rh-font-family-body-text);
    font-size: var(--rh-font-size-body-text-md);
    line-height: var(--rh-line-height-body-text);
    margin-block-end: var(--rh-space-lg);
  }

  & .desktop { display: none; }

  & rh-disclosure {
    margin-block-end: var(--rh-space-xl);
  }

  @media (width >= /*--rh-media-md*/ 992px) {
    display: grid;
    grid-template-columns: auto 1fr;
    gap: var(--rh-space-lg);
    align-items: start;

    & .desktop {
      display: block;
      position: sticky;
      top: 0;
    }

    & .mobile { display: none; }
  }
}

demo/rh-jump-links-with-content-inline-1.content:
#page-content {
  display: grid;
  grid-template-columns: max-content max-content;
  gap: var(--rh-space-lg);
  #jump-links-container, article {
    padding: var(--rh-space-2xl);
  }
}
#jump-links-container {
  position: sticky;
  inset-block-start: 0;
  align-self: start;
}
#jump-links-title {
  font-weight: var(--rh-font-weight-heading-medium);
  font-family: var(--rh-font-family-body-text);
  font-size: var(--rh-font-size-body-text-md);
  line-height: var(--rh-line-height-body-text, 1.5);
  margin-block-end: var(--rh-space-lg);
}

demo/reset.css.content:
/* Box sizing rules */
*,
*:before,
*:after {
  box-sizing: border-box;
}

/* Remove default margin */
:where(body,
h1,
h2,
h3,
h4,
h5,
h6,
p,
li,
figure,
figcaption,
blockquote,
dl,
dd) {
  margin: 0;
}

/* Default scroll behavior */
html:focus-within {
  scroll-behavior: smooth;
}

/* Inherit fonts for inputs and buttons */
:where(input,
button,
textarea,
select) {
  font: inherit;
}

/* Remove all animations and transitions for people that prefer not to see them */
@media (prefers-reduced-motion: reduce) {
  html:focus-within {
    scroll-behavior: auto;
  }

  *,
  *:before,
  *:after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }
}

