demo/rh-navigation-primary-index-inline-.content:
/* styles for mock content */
#mock {
  p {
    margin-block-start: var(--rh-space-4xl, 64px);
    padding-inline: var(--rh-space-lg, 16px);
    @media (min-width: 768px) {
      padding-inline: var(--rh-space-2xl, 32px);
    }
  }
}

/* panel grid */
.grid {
  display: grid;
  grid-template-columns: 1fr;
  gap: var(--rh-space-4xl);
  font-family: var(--rh-font-family-body-text, RedHatText, 'Red Hat Text', Helvetica, Arial, sans-serif);
  fon-size: var(--rh-font-size-body-text-md, 1rem);

  @container (min-width: 768px) {
    grid-template-columns: 1fr 1fr 1fr;
  }

  #profile {
    margin-inline-start: var(--rh-space-4xl);
    display: grid;
    grid-template-columns: max-content 1fr;
    grid-template-areas: 'avatar content';
    grid-template-rows: fit-content;
    column-gap: var(--rh-space-lg, 16px);

    & rh-avatar {
      --rh-size-icon-06: 72px;

      grid-area: avatar;
      display: block;
    }

    & .lockup {
      display: contents;

      & .account {
        grid-area: content;

        & .name {
          font-family: var(--rh-font-family-heading, RedHatDisplay, 'Red Hat Display', Helvetica, Arial, sans-serif);
          font-size: var(--rh-font-size-heading-sm, 1.5rem);
        }
      }

      & .info {
        grid-area: content;
        grid-row: 2;
      }

      & .language {
        grid-area: content;
        grid-row: 3;
      }

      & .logout {
        grid-area: content;
        grid-row: 4;
      }
    }
  }

  .content-column {
    max-width: 320px;
    margin-block: var(--rh-space-md);
    line-height: var(--rh-line-height-body-text);
  }

  & select {
    width: 100%;
  }

  & ul {
    display: flex;
    flex-direction: column;
    gap: var(--rh-space-2xl);
    list-style: none;
    margin: 0;
    padding: 0;

    & p {
      font-size: var(--rh-font-size-body-text-sm, 0.875rem);
    }
  }
}

rh-navigation-primary-lightdom.css.content:
rh-navigation-primary {
  & rh-navigation-primary-item {
    color: var(--rh-color-text-primary) !important;

    & a {
      color: var(--rh-color-interactive-primary-default) !important;
      text-decoration: none;
      inline-size: 100%;

      @container navigation-primary (min-width: 1200px) {
        padding: 0 var(--rh-space-lg);
      }

      &:hover {
        color: var(--rh-color-interactive-primary-hover) !important;
      }
    }

    &[slot='event']::part(container) {
      padding: 0;
    }

    &[slot='links'] {
      display: flex;
      align-items: center;

      &:after {
        display: block;
        content: '';
        border-inline-end:
          var(--rh-border-width-sm, 1px)
          solid
          light-dark(var(--rh-color-gray-20, #e0e0e0), var(--rh-color-gray-60, #4d4d4d));
        height: 30px;
      }

      &:nth-child(1 of rh-navigation-primary-item[slot='links']) {
        --_padding-inline-start: 0;

        & a {
          margin-inline-start: 0;
        }
      }

      &:nth-last-child(1 of rh-navigation-primary-item[slot='links']) {
        --_padding-inline-end: 0;

        &:after {
          border-inline-end-color: transparent;
        }

        & a {
          margin-inline-end: 0;
        }
      }
    }
  }
}

