demo/rh-tabs-color-context-inline-0.js.content:
import '@rhds/elements/rh-cta/rh-cta.js';
import '@rhds/elements/rh-tabs/rh-tabs.js';
import '@rhds/elements/lib/elements/rh-context-demo/rh-context-demo.js';

const form = document.querySelector('form');
const tabs = document.querySelector('rh-tabs');

form.addEventListener('input', function sync(event) {
  event?.preventDefault();
  const data = Object.fromEntries(new FormData(form));
  tabs.box = data.variant || undefined;
  tabs.centered = 'centered' in data;
  tabs.vertical = 'vertical' in data;
});

