demo/rh-card-color-context-inline-0.css.content:
rh-context-demo::part(demo) {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(360px, 1fr));
  gap: var(--rh-space-lg, 16px);
}

rh-card {
  &.full {
    &::part(header) { margin: 0; }
    & :is(h1, h2, h3, h4, h5, h6)[slot="header"] {
      padding-inline: var(--rh-space-xl, 24px);
    }
    & img {
      display: block;
      min-width: 100%;
      height: auto;
    }
  }
  &.bar {
    &::part(header) {
      margin: 0;
      padding: var(--rh-space-lg, 16px) var(--rh-space-xl, 24px);
      text-transform: uppercase;
      font-weight: var(--rh-font-weight-heading-regular, 300);
      font-size: var(--rh-font-size-body-text-md, 1rem);
      background-color: var(--bar-header-context-background-color);
    }
    &::part(body) {
      /* margin: var(--rh-space-lg, 16px) var(--rh-space-xl, 24px); */
    }
  }
}

[color-palette^="light"] {
  --bar-header-context-background-color: var(--rh-color-surface-light, #e0e0e0);
}
[color-palette^="dark"] {
  --bar-header-context-background-color: var(--rh-color-surface-dark, #383838);
}

label[for="picker"] {
  display: block;
}

demo/rh-card-grid-inline-1.css.content:
#card-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: var(--rh-space-lg, 16px);
  max-width: 1058px;
}

demo/rh-card-variants-inline-1.css.content:
#card-variants {
  padding: 0.5em;
  display: grid;
  gap: 1em;
  grid-template-columns: repeat(auto-fit, minmax(360px, 1fr));
  grid-auto-columns: 1fr;
}

rh-card {
  height: max-content;
}

rh-card.bar {
  --rh-card-heading-font-family: var(--rh-font-family-body-text, RedHatText, "Red Hat Text", "Noto Sans Arabic", "Noto Sans Hebrew", "Noto Sans JP", "Noto Sans KR", "Noto Sans Malayalam", "Noto Sans SC", "Noto Sans TC", "Noto Sans Thai", Helvetica, Arial, sans-serif);
  --rh-card-heading-font-weight: var(--rh-font-weight-body-text-regular, 400);
  --rh-card-heading-font-size: var(--rh-font-size-body-text-md, 1rem);
  &::part(header) {
    margin: 0;
    padding: var(--rh-space-lg, 16px) var(--rh-space-xl, 24px);
    background-color: var(--_header-bg, var(--rh-color-surface-light, #e0e0e0));
    text-transform: uppercase;
  }
}

rh-card.image {
  --rh-card-heading-font-weight: var(--rh-font-weight-heading-medium, 500);
  --rh-card-heading-font-size: var(--rh-font-size-heading-md, 1.75rem);
  &::part(header) {
    margin: 0;
  }

  & img {
    display: block;
    min-width: 100%;
    height: auto;
  }

  &.heading {
    & h2 {
      margin-block-start: 0;
      margin-block-end: var(--rh-space-lg, 16px);
    }
  }
}

rh-card.center::part(body),
rh-card.center::part(footer) {
  justify-content: center;
  text-align: center;
}

rh-card.end::part(body),
rh-card.end::part(footer) {
  justify-content: end;
  text-align: end;
}

