  line-height: var(--rh-line-height-heading, 1.3);
}

:where(code) {
  font-family: var(--rh-font-family-code, RedHatMono, "Red Hat Mono", "Courier New", Courier, monospace);
  line-height: var(--rh-line-height-code, 1.5);
}

:where(kbd) {
  font-family: var(--rh-font-family-body-text, RedHatText, "Red Hat Text", "Noto Sans Arabic", "Noto Sans Hebrew", "Noto Sans JP", "Noto Sans KR", "Noto Sans Malayalam", "Noto Sans SC", "Noto Sans TC", "Noto Sans Thai", Helvetica, Arial, sans-serif);
  font-size: 1rem;
  line-height: var(--rh-line-height-body-text, 1.5);
}

:where(h1) {
  font-size: var(--rh-font-size-heading-2xl, 3rem);
}

:where(h2) {
  font-size: var(--rh-font-size-heading-xl, 2.5rem);
}

:where(h3) {
  font-size: var(--rh-font-size-heading-lg, 2.25rem);
}

:where(h4) {
  font-size: var(--rh-font-size-heading-md, 1.75rem);
}

:where(h5) {
  font-size: var(--rh-font-size-heading-sm, 1.5rem);
}

:where(h6) {
  font-size: var(--rh-font-size-heading-xs, 1.25rem);
}

:where(p) {
  font-size: var(--rh-font-size-body-text-md, 1rem);
}

:where(p, h1, h2, h3, h4, h5, h6, li) {
  max-width: 56rem;
}

demo/rh-dialog-events-inline-0.js.content:
import '@rhds/elements/rh-button/rh-button.js';
import '@rhds/elements/rh-cta/rh-cta.js';
import '@rhds/elements/rh-dialog/rh-dialog.js';

const dialog = document.getElementById('dialog');
const form = document.getElementById('dialog-events');
const events = [];
form.addEventListener('submit', e => e.preventDefault());
const onDialogEvent = event => {
  events.push(event.type);
  form.elements.events.value = events.join(', ');
};
dialog.addEventListener('close', onDialogEvent);
dialog.addEventListener('open', onDialogEvent);
dialog.addEventListener('cancel', onDialogEvent);

